import React from "react";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { styled } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import InfoIcon from '@mui/icons-material/Info';
import Tooltip, { TooltipProps, tooltipClasses } from '@mui/material/Tooltip';
import Divider from '@mui/material/Divider';

function Input(){
    const [input, updateInput] = React.useState("");
    const [uiSchema, updateSchema] = React.useState("");
    // const [clicked, updateClicked] = React.useState(false);

    function changeInput(event){
        const text = event.target.value;
        updateInput(text);
    }

    function getUISchema(){
        updateSchema(input);
        updateInput("");
    }

    function htmlToolTip(){
        return (styled(({ className, ...props }: TooltipProps) => (
            <Tooltip placement="right" {...props} classes={{ popper: className }} />
            ))(({ theme }) => ({
            [`& .${tooltipClasses.tooltip}`]: {
              backgroundColor: '#f5f5f9',
              color: 'rgba(0, 0, 0, 0.87)',
              maxWidth: 220,
              fontSize: theme.typography.pxToRem(12),
              border: '1px solid #dadde9',
            },
        })));
    }
    
    function generateInput(sort, label, description, validate, jsonKey, icon, level, placeholder){
        const {required, immutable} = validate;
        placeholder = (!placeholder) ? ("Enter " + label) : placeholder;
        const describe = (!description) ? false : true;
        const HtmlTooltip = htmlToolTip();

        return ( 
            <div className="formField">
                <span className="label">{label}
                    <span className="required">{(required?"*    ":"     ")}</span>
                </span>
                {(describe) ?
                    <HtmlTooltip
                    title={
                    <React.Fragment>
                    <div className="infoHeading">{label}</div>
                    <Divider />
                    <div className="info">{description}</div>
                    </React.Fragment>
                    }
                >
                   <Fab style={{height:"20px", width:"20px", minHeight:"20px", backgroundColor:"#fbfdff"}} color="primary" aria-label="add">
                    <InfoIcon color="primary" style={{height:"20px", width:"20px"}}/>
                    </Fab>
                </HtmlTooltip> : null}
                <input className="inputField" type="text" required={required} 
                placeholder={placeholder} readOnly={immutable}></input>
            </div>
        );
    }

    function generateSelect(sort, label, description, validate, jsonKey, icon, level, placeholder){
        const {required, options, defaultValue, immutable} = validate;
        placeholder = (!placeholder) ? ("Enter " + label) : placeholder;
        const describe = (!description) ? false : true;
        const HtmlTooltip = htmlToolTip();

        return ( 
            <div className="formField">
                <span className="label">{label}
                    <span className="required">{(required?"*    ":"     ")}</span>
                </span>
                {(describe) ?
                    <HtmlTooltip
                    title={
                    <React.Fragment>
                    <div className="infoHeading">{label}</div>
                    <Divider />
                    <div className="info">{description}</div>
                    </React.Fragment>
                    }
                >
                   <Fab style={{height:"20px", width:"20px", minHeight:"20px", backgroundColor:"#fbfdff"}} color="primary" aria-label="add">
                    <InfoIcon color="primary" style={{height:"20px", width:"20px"}}/>
                    </Fab>
                </HtmlTooltip> : null}
                <input className="inputField" type="text" required={required} 
                placeholder={placeholder} readOnly={immutable}></input>
            </div>
        );
    }

    function invalidInput(){
        return (<h1>Enter Valid Input!!!!!!!!!!!</h1>);
    }

    function isJsonString(str) {
        try {
            JSON.parse(str);
        } catch (e) {
            return false;
        }
        return true;
    }

    function generateFields(jsonSchema){
        if(isJsonString(jsonSchema)){
            const jsSchema = JSON.parse(jsonSchema);
            
            return (
                jsSchema.map((field, index) => {
                    const {sort, label, description, validate, jsonKey, uiType, icon, level, placeholder} = field;
                    switch(uiType){
                        case "Input":
                            console.log("Input Switch Case");
                            return generateInput(sort, label, description, validate, jsonKey, uiType, icon, level, placeholder);
                        
                        case "Select":
                            console.log("Select Switch Case");
                            return generateSelect(sort, label, description, validate, jsonKey, uiType, icon, level, placeholder);    

                        default:
                            return invalidInput();
                    }
                })
            );
        } else {
            console.log("Enter a valid input!");
            return;
        }
    }

    

    return (
    <div className="container">
    <div className="section">
        <form>
            <TextField
              id="outlined-multiline-flexible fullWidth margin-dense"
              label="UI Schema"
              onChange={changeInput}
              value={input}
              margin="dense"
              fullWidth
              multiline
              maxRows={14}
            />
            <Button id="generator" onClick={getUISchema} variant="contained" size="small">Generate Form</Button>
        </form>
    </div>
    <div id="output" className="section">
        <form>
            {generateFields(uiSchema)}
            <Button id="generator" variant="contained" size="small">Submit</Button>
        </form>
    </div>
    </div>
    );
}

export default Input;
