import React from "react";
import { ReactDOM } from "react";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

function Input(){
    const [input, updateInput] = React.useState("");

    function changeInput(event){
        const text = event.target.value;
        updateInput(text);
    }

    function generateFields(){
        const uiSchema = JSON.parse(input);
        console.log(uiSchema);
        if(!uiSchema){
            ReactDOM.render(<h1>Hello</h1>, document.getElementById("output"));
        }
    }

    return (
    <div className="container">
    <div className="section">
        <form>
            <TextField
              id="outlined-multiline-flexible fullWidth margin-dense"
              label="UI Schema"
              onChange={changeInput}
              value={input}
              margin="dense"
              fullWidth
              multiline
              maxRows={14}
            />
            <Button id="generator" onClick={generateFields} variant="contained" size="small">Generate Form</Button>
        </form>
    </div>
    <div id="output" className="section">
        <form>
            <Button id="generator" variant="contained" size="small">Submit</Button>
        </form>
    </div>
    </div>
    );
}

export default Input;
