import React from "react";
import { ReactDOM } from "react";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

function Input(){
    const [input, updateInput] = React.useState("");
    const [uiSchema, updateSchema] = React.useState("");
    // const [clicked, updateClicked] = React.useState(false);

    function changeInput(event){
        const text = event.target.value;
        updateInput(text);
    }

    function getUISchema(){
        updateSchema(input);
        updateInput("");
    }
    
    function generateInput(sort, label, description, validate, jsonKey, icon, level, placeholder){
        const {required, immutable} = validate;
        placeholder = (placeholder==="") ? ("Enter" + label) : placeholder;
        return (
            <div className="formField">
                <span className="label">{label + (required?"*":"")}</span>
                {(description!=="") ? <span>info</span> : null}
                <input className="inputField" type="text" required={required} 
                placeholder={placeholder} readOnly={immutable}></input>
            </div>
        );
    }

    function invalidInput(){
        return (<h1>Enter Valid Input!!!!!!!!!!!</h1>);
    }

    function isJsonString(str) {
        try {
            JSON.parse(str);
        } catch (e) {
            return false;
        }
        return true;
    }

    function generateFields(jsonSchema){
        if(isJsonString(jsonSchema)){
            const jsSchema = JSON.parse(jsonSchema);
            
            return (
                jsSchema.map((field, index) => {
                    const {sort, label, description, validate, jsonKey, uiType, icon, level, placeholder} = field;
                    switch(uiType){
                        case "Input":
                            console.log("Input Switch Case");
                            return generateInput(sort, label, description, validate, jsonKey, uiType, icon, level, placeholder);
                            
                        default:
                            return invalidInput();
                    }
                })
            );
        } else {
            console.log("Enter a valid input!");
            return;
        }
    }

    

    return (
    <div className="container">
    <div className="section">
        <form>
            <TextField
              id="outlined-multiline-flexible fullWidth margin-dense"
              label="UI Schema"
              onChange={changeInput}
              value={input}
              margin="dense"
              fullWidth
              multiline
              maxRows={14}
            />
            <Button id="generator" onClick={getUISchema} variant="contained" size="small">Generate Form</Button>
        </form>
    </div>
    <div id="output" className="section">
        <form>
            {generateFields(uiSchema)}
            <Button id="generator" variant="contained" size="small">Submit</Button>
        </form>
    </div>
    </div>
    );
}

export default Input;
