import React from "react";
import Fab from '@mui/material/Fab';
import InfoIcon from '@mui/icons-material/Info';
import Divider from '@mui/material/Divider';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import toolTip from "./ToolTip";

function SelectField(props){
    const [age, setAge] = React.useState("");
    const handleChange = (event) => {
        setAge(event.target.value);
    };
    const {required, options, defaultValue, immutable} = props.validate;
    const placeholder = (!props.placeholder) ? ("Enter " + props.label) : props.placeholder;
    const describe = (!props.description) ? false : true;
    const Tooltip = toolTip();

    return ( 
        <div className="formField">
            <span className="label">{props.label}
                <span className="required">{(required?"*    ":"     ")}</span>
            </span>
            {(describe) ?
                <Tooltip
                title={
                <React.Fragment>
                <div className="infoHeading">{props.label}</div>
                <Divider />
                <div className="info">{props.description}</div>
                </React.Fragment>
                }
                >   
                <Fab style={{height:"20px", width:"20px", minHeight:"20px", backgroundColor:"#fbfdff"}} color="primary" aria-label="add">
                <InfoIcon color="primary" style={{height:"20px", width:"20px"}}/>
                </Fab>
                </Tooltip> : null}
            <InputLabel id="demo-simple-select-label">Age</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={age}
              label="Age"
              onChange={handleChange}
            >
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
            </Select>
        </div>
    );
}

export default SelectField;
